package week1;

public class DataTypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Boolean True/False
		
		 boolean status=true;
		 System.out.println(status);
		 System.out.println("Boolean data is " + status);
     //by default boolean data is false
		 
	System.out.println("******************");
	
	
	//Charcter Declaration
	
	char c1='A'; // Single Char
	System.out.println("Character data is " + c1);
	
	char c2='@'; // Special Char
	System.out.println("Special Charactyer is " + c2);
	char c3=90;  // ASCII character
	System.out.println("ASCII character is " + c3);
	
	// byte data 1 byte -128 to 127
	
	byte b1=125;
	System.out.println("Byte data is " + b1);
	
	
	//Short data 2byte
	
	short s1=1000;
	System.out.println("Short data is "+ s1);
	
	//long 8 byte , Suffix=L
	long l1=99999999998989L;
	System.out.println("Long data is " + l1);
	
	// Float 4 byte
	float f1=56.66f;
	System.out.println("Float value is "+ f1);
	
	// Double 8 byte
	double d1=9888.897;
	System.out.println("Double data is "+ d1);

	}
}
